def two_sums(nums):
    res = []
    sum_map = {}
    target = 0
    for i, num in enumerate(nums):
        remains = target - num
        if remains in sum_map:
            res.append([sum_map[remains], i])
        else:
            sum_map[num] = i
    return res

def test_two_sums():
    two = two_sums([-4, -1, -1, 0, 4, 2, 1])
    print(two)
#test_two_sums()

def three_sums(nums):
    res = []
    target = 0
    found = set()
    ln = len(nums)
    seen = {}
    for i in range(ln):
        for j in range(i+1, ln):
            remains = target - (nums[i] + nums[j])
            if remains in seen and seen[remains] != i:
                min_v = min(nums[i], nums[j], remains)
                max_v = max(nums[i], nums[j], remains)
                if (min_v, max_v) not in found:
                    res.append([nums[i], nums[j], remains])
                    found.add((min_v, max_v))
            else:
                seen[nums[j]] = i

    return res

def threeSum2(nums):
    res = []
    found, dups = set(), set()
    seen = {}
    for i, val1 in enumerate(nums):
        if val1 not in dups:
            dups.add(val1)
            for j, val2 in enumerate(nums[i+1:]):
                complement = -val1 - val2
                if complement in seen and seen[complement] == i:
                    min_val = min((val1, val2, complement))
                    max_val = max((val1, val2, complement))
                    if (min_val, max_val) not in found:
                        found.add((min_val, max_val))
                        res.append([val1, val2, complement])
                seen[val2] = i
    return res

def threeSum(nums):
    res = []
    nums.sort()
    for i in range(len(nums)):
        if nums[i] > 0:
            break
        if i == 0 or nums[i - 1] != nums[i]:
            twoSumII(nums, i, res)
    return res

def twoSumII(nums, i, res):
    lo, hi = i + 1, len(nums) - 1
    while (lo < hi):
        sum = nums[i] + nums[lo] + nums[hi]
        if sum < 0 or (lo > i + 1 and nums[lo] == nums[lo - 1]):
            lo += 1
        elif sum > 0 or (hi < len(nums) - 1 and nums[hi] == nums[hi + 1]):
            hi -= 1
        else:
            res.append([nums[i], nums[lo], nums[hi]])
            lo += 1
            hi -= 1

